                TEST_CHECK_NEARLY_EQUAL(hypg_a_1_1_1_b_2_2_2(-5.0), 0.642604, eps);

                TEST_CHECK_NEARLY_EQUAL(hypg_a_1_1_b_2_2_d_0_0_0_1_0(-5.), 0.15402754920324, eps);
                TEST_CHECK_NEARLY_EQUAL(hypg_a_1_1_b_2_2_d_0_0_0_1_1(-5.), 0.00131633570387587, eps);
                TEST_CHECK_NEARLY_EQUAL(hypg_a_1_1_b_2_2_d_0_0_0_1_2(-5.), -0.00280657110658356, eps);
                TEST_CHECK_NEARLY_EQUAL(hypg_a_1_1_b_2_2_d_0_0_0_1_3(-5.), -0.00198777108879206, eps);
